import React from 'react';
import logo from './logo.svg';
import './App.css';
import PropTypes from 'prop-types';

const Content = ({ children }) =>(
<div style={{ margin: 10, fontSize: 12 }} > {children} </div>
);

Content.propType = {
  children: PropTypes.node
};

const views = [
  <Content key="view1">Home</Content>
]

const menuListWidth = {
  touch: 150,
  low: 150,
  medium: 120,
  high: 100
};

const actionsWidth = {

  touch: 250,
  low: 250,
  medium: 235,
  high: 205
};

const App = () => {
  const density = useDensity();
  const [viewIndex, setViewIndex] = useState(0);

  const handleNavItemClick = (payload, event) => {
    setViewIndex(payload.index);
    console.log('navigation', 'onItemClick', payload, { event});
  };

const handleActionItemClick = props => {
  console.log('action', 'onItemClick', props);
};

  const navigationProps = {
    currentIndex: viewIndex,
    onItemClick: handleNavItemClick,
    menuPops: {
      listProps: {
        width: menuListWidth[density]
      }
    }
  };

  const navigationSource = [
    { itemId: 'Home', Label: 'Home'}
  ];

  const actionProps = {
    iconSize: 17,
    maxWidth: actionsWidth[density],
    onItemClick: handleActionItemClick
  };
const actionSource = [
  {
  itemId: ' message',
  ariaLabel: 'Message 25',
  overflowLabel: 'Messages',
  badgeContent: 25
  },
  { itemId: 'search', overflowLabel: 'Search', iconName: 'search'},
  { itemId: 'filter', overflowLabel: 'Filter', iconName: 'filter'},
  { itemId: 'user', overflowLabel: 'User', iconName: 'user'},
  { itemId: 'group', overflowLabel: 'Group', iconName: 'group'},
];

return (
<div style={{ width: '100%' }}>
  <AppHeaderNext
    actionProps={actionProps}
    actionSource={actionSource}
    appTitle='AG Inc'
    navigationProps={navigationProps}
    navigationSource={navigationSource}
    />
    {views[viewIndex]}
</div>

  );
}

export default App;
